const SKILL_URL_BASE = 'https://wiki.guildwars.com';
const SKILL_URL_WIKI = '/wiki';
const SKILL_URL_ICON = '/images';

function getWikiUrl(skill) {
    return SKILL_URL_BASE + SKILL_URL_WIKI + skill.url;
}

function getIconUrl(skill) {
    return SKILL_URL_BASE + SKILL_URL_ICON + skill.icon;
}

function lookupSkillId(name) {
    for (let i = 0; i < SKILL_MASTER_COUNT; i++) {
        let skill = SKILL_MASTER_LIST[i];
        if (name == skill.name) {
            return i;
        }
    }
    console.log("Couldn't find skill id for skill name: " + name);
    return null;
}

